package corejava_package1;

public class Code25 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 4; 
		for (int i=n;i<=n;i--) {
			for ( int j =1;j<=i;j++){
					System.out.print("*");
				}
			
				System.out.println();
		
			
		}

	}
	}

